INSERT INTO `zapis` VALUES (1,'Pariški abecedarij',1,' ','XI. stoljeće','Obla glagoljica','Latinski jezik','Imena 31 glagoljskog slova pisana latinicom',' ','U literaturi se ponekad naziva i bugarskim, tj. Abecedarium bulgaricum, najstariji je ksenografski abecedarij što znači da ga je sastavljao stranac s namjerom upoznavanja nove publike s glagoljičkim pismom',NULL,NULL),(2,'Valunska ploča',1,'Crkva Sv. Marije u Valunu na Cresu','XI. stoljeće','Glagoljica i latinica','Hrvatskostaroslavenski i latinski jezik','Nadgrobni natpis','Kamena ploča','Dvojezični i dvopismeni nadgrobni natpis',NULL,NULL),(3,'Kločev glagoljaš',1,' ','XI. stoljeće','Obla glagoljica uz neke uglate oblike','Hrvatskostaroslavenski jezik','Pet homilija','14 listova kodeksa (najvjerojatnije sadržavao 552)','Spomenik koji ima neke uglate glagoljične oblike, sadrži originalnu Anonimnu homiliju za koju se pretpostavlja da je autor Sv. Metod',NULL,NULL),(4,'Plominski natpis',1,'Crkva Sv. Jurja u Plominu','XI. stoljeće','Obla glagoljica','Hrvatskostaroslavenski jezik','Fragmentarno uklesane 4 riječi autora: SE E PISЪLЪ S','Kameni reljef','Tekst na reljefu s uklesanim likom Silvana/Sv. Jurja',NULL,NULL),(5,'Konavoski glagoljski natpis',1,'Dunave u Konavlima','XI. stoljeće','Obla glagoljica','Hrvatskostaroslavenski jezik','Natpis čiji sadržaj govori o nastanku tog spomenika','Kamena ploča','Istočno (južno) krilo glagoljaštva, pretpostavlja se da je nastao 1060. godine, čuva se u Arheološkom muzeju u Dubrovniku',NULL,NULL),(6,'Krčki natpis',1,'Krk','XI. stoljeće','Obla glagoljica','Hrvatskostaroslavenski jezik','Natpis o gradnji crkve uz benediktinski samostan','Kamena ploča','Ploča na kojoj su zapisana imena graditelja benediktinskog samostana (maĵъ, opatъ radonê, rugota, dobroslav[ъ]))',NULL,NULL),(7,'Župski glagoljski natpis',1,'Župa Dubrovačka','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Nepoznat, neistražen','Kamena ploča','Glagoljski natpis na ulomku antičkog crijepa (teguli), pronađen kraj crkve Sv. Đurđa između mjesta Petrače i Buića u Župi Dubrovačkoj; još neistražen u potpunosti, čuva se u Arheološkom muzeju u Dubrovniku',NULL,NULL),(8,'Jurandvorski ulomci',1,'Crkva Sv. Lucije u Jurandvoru, Krk','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Nepoznati dijelovi ulomaka, spominje se kralj Zvonimir','4 ulomka ploče','Popularno se još zove i druga Bašćanska ploča',NULL,NULL),(9,'Senjska ploča',1,'Senj','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Ulomci teksta, sadrži kršćansku invokaciju','Ploča, gornji dio lijevog pluteja (oltarne pregrade)','Motivi lozice, pletera, desetak redaka teksta, pronađena prilikom konzerviranja tvrđave Nehaj 1964. godine',NULL,NULL),(10,'Grdoselski ulomak',1,'Istra','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Spominje se pop Golob iz Tinjana, kapelan grdoselskog kneza Pankracija','Ulomak kamene grede ili ploče s dijelom glagoljskoga natpisa','Prijelazni tip oble u uglatu glagoljicu, mali ulomak gdje je posveta oltara u čast dvjema sveticama',NULL,NULL),(11,'Kninski ulomak',1,'Crkva Sv. Bartola u Kapitulu kod Knina','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Ulomak luka s par glagoljičnih i ćiriličnih slova: oĉju premi','Maleni ulomak ukrašen romaničkom lozicom','Miješanje glagoljice i ćirilice',NULL,NULL),(12,'Plastovski odlomak',1,'Plastovo kod Skradina','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – rub zapadnog područja hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Maleni ulomak s tekstom: P E T R A ? I J U M U U Ž U T U E G A B R A','Maleni kameni ulomak','Miješanje glagoljičnih i ćiriličnih slova',NULL,NULL),(13,'Bečki listići',1,' ','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Odlomak gregorijanskog sakramentara (misala) zapadnog obreda, sadrže tri mise u čast apostola','Dva lista pergamenta','Po sadržaju bliski Kijevskim listićima (liturgija Sv. Petra), svjedoče o češko-hrvatskim uljudbenim vezama; U literaturi se nerijetko ističe kako su Bečki listići najstariji hrvatskoglagoljski tekst liturgijske namjene; našao ih je i opisao Vatroslav Jagić 1890. godine',NULL,NULL),(14,'Prva stranica Kijevskih listića',1,' ','XI. - XII. stoljeće','Obla/formativna glagoljica (istočno područje hrvatskoga glagoljaštva)','Staroslavenski/Hrvatskostaroslavenski jezik','Poslanica Sv. Pavla apostola Rimljanima i oracije Blažene Djevice Marije (prepletanje istočne i zapadne tradicije)','Listovi neujednačene veličine','Kijevski listići drže se najstarijim spomenikom kanona starocrkvenoslavenskih spisa, nastali su na češkom području i broje ukupno 13 stranica, prva stranica pisana je kasnije na hrvatskom prostoru i zaključuje se da je taj dio sakramentara bio u uporabi na dubrovačkom području',NULL,NULL),(15,'Glagoljički natpis iz Čečavca kod Slavonske Požege',1,'Čečavac','XI. - XII. stoljeće','Formativna glagoljica','Hrvatskostaroslavenski jezik','Četiri se glagoljična slova tumače kao godina prema brojevnoj vrijednosti ČRIZ = 1129','4 glagoljična slova na komadu opeke','Potječe iz romaničke benediktinske opatije Sv. Miholvila u Rudini. Čuva se u Muzeju Požeške kotline u Požegi',NULL,NULL),(16,'Bašćanska ploča',2,'Jurandvor kod Baške na otoku Krku','1100. godina','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Kralj Zvonimir daruje ledinu za gradnju crkve Sv. Lucije','Kamena ploča','Ploča je služila kao oltarna pregrada; sadrži nekoliko ćiriličnih slova; Značajni istraživači: Franjo Rački, Ivan Crnčić Vatroslav Jagić, Vjekoslav Štefanić, Branko Fučić; danas se čuva u Auli Hrvatske akademije znanosti i umjetnosti',NULL,NULL),(17,'Bašćanski ostrišci',2,'Baška','XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Sadrže evanđeoske stihove, odlomak Muke po Mateju (Mt 26, 37-48)','Dvije pergamenske vrpce, dva uska pergamentna ostriška','Nazivaju se još i Premudini ostrišci prema popu glagoljašu i filologu Vinku Premudi – oporučno ih je ostavio Jugoslavenskoj akademiji znanosti i umjetnosti u Zagrebu. Vinko Premuda ostriške je pronašao u Baški, ali su se nakon njegove smrti zagubili te su ponovno pronađeni',NULL,NULL),(18,'Glagoljički ulomak iz Kijevaca kod Bosanske Gradiške',2,'Kijevci','XII. stoljeće','Formativna glagoljica',' ','Nepotpun natpis uparan u prirodni blok građevnoga kamena','Nekoliko ispisanih formativnih glagoljskih slova','Zabilježeno muško ime Raden u dativu. Kijevački natpis leži na samom rubu panonskoga prostora, a Branko Fučić dvoji treba li ga pripisati hrvatskome ili starijem moravsko-panonskome glagoljskom području',NULL,NULL),(19,'Minhenski abecedarij',2,' ','XII. stoljeće','Obla glagoljica (i ćirilica)',' ','Glagoljični i ćirilični abecedarij','1 list pergamenta','Napisan rukom na zadnju stranicu latiničnog (latinskog) kodeksa iz X. stoljeća; ksenografski alfabet; prvi ga je u slavistici objavio Vatroslav Jagić 1911. godine',NULL,NULL),(20,'Darovnica slavnog Dragoslava',2,'Dobrinj na Krku','1100. godina','Glagoljica (kurzivna u prijepisu)','Hrvatskostaroslavenski jezik','Dragoslav daruje svoje imanje crkvi Sv. Vida','Isprava/dokument/listina','Najstariji pisani spomenik o postojanju župa na Krku, spominju se po prvi put Dobrinj i Vrbnik, nije sačuvan u izvorniku',NULL,NULL),(21,'Humski grafit',2,'Crkva Sv. Jerolima (Jeronima) u Humu','XII. stoljeće','Glagoljica (najstariji tip pisma glagoljskih spomenika Istre i Kvarnera)','Hrvatskostaroslavenski jezik','Grafit popa glagoljaša, evidentirane odslužene gregorijanske mise','Glagoljični grafit na zidu oslikanom freskama','Uparan je oštricom uz 30 okomitih crta; Zabilježeno je da se za dušu pokojnog humskog kovača Martina izrekao gregorijanski niz misa, ali povrh njega i još jedna misa (je vzeta)',NULL,NULL),(22,'Grškovićev odlomak apostola',2,'Istočna Bosna, Zahumlje ili Zeta','XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - istočno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Odlomci iz Djela apostolskih namijenjeni uporabi u crkvi','Dva pergamentna dvolista','Odlomke potkraj XIX. stoljeća pronašao vrbnički kapelan Niko Gršković, čuvaju se u Arhivu HAZU',NULL,NULL),(23,'Mihanovićev odlomak apostola',2,'Bosansko-humsko područje (ili zetsko)','XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - istočno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Odlomak kratkog apostolara, sadrži perikope iz Poslanica Pavla apostola Korinćanima, Efežanima, Galaćanima (uzus istočne Crkve)','Pergamentni dvolist','Pronađen zalijepljen u koricama Ilovičke krmčije, nastao u doba kad je prevladavala ćirilična pismenost, čuva se u Arhivu HAZU',NULL,NULL),(24,'Supetarski ulomak',2,'Sv. Petar u Šumi','XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Dio razbijene, prelomljene kamene grede, ulomak: amenъ їĕkovъm[ъ]','Mali kameni ulomak','Riječ amen pisana ćirilicom, a ime Jakov glagoljicom, čuva se u Etnografskom muzeju Istre u Pazinu',NULL,NULL),(25,'Svetokrižki odlomak',2,'Sv. Križ pokraj Trsata','XII. - XIII. stoljeće','Formativna glagoljica','4 slova - jat, S, A i E','Nepotpuni ulomak s glagoljičnim slovima','Maleni glineni ulomak','Svetokrižki ulomak je 1. prosinca 2004. godine pronašao učenik riječke Osnovne škole Gornja Vežica Antonio Torre na brdu Sveti križ iznad istočnog dijela grada za izviđanje terena u sklopu uređenja poučne staze na brdu',NULL,NULL),(26,'Kukuljevićev odlomak misala',3,'Zapadno krilo hrvatskog glagoljaštva','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Odlomak misala (temporala) iz šeste korizmene nedjelje i početka Velikog ponedjeljka','List pergamenta','Paleografski pokazatelji govore o starini: veličina slova neujednačena, ligature rijetke; iz zbirke Ivana Kukuljevića, jedan je od najstarijih fragmenata pisan standardiziranom ustavnom glagoljicom, čuva se u HAZU',NULL,NULL),(27,'Vinodolski zakon',3,'Općina Vinodol','1288. godina','Ustavna/kurzivna glagoljica','Starohrvatski jezik','Zbornik običajnog prava vinodolskih općina','Tekst sa 77 članaka','Najstariji hrvatski glagoljični zakonski tekst i drugi po starini slavenski zakonik, poslije Ruske pravde, nije sačuvan u izvorniku, objavio ga prvi put Mažuranić 1843., čuva se u Nacionalnoj i sveučilišnoj knjižnici u Zagrebu',NULL,NULL),(28,'Ljubljanski homilijar',3,' ','XIII. stoljeće','Ustvna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Dio homilijara (za prve tri korizmene nedjelje), sadrži odlomak homilije Ivana Zlatoustog na Glavosjek Ivana Krstitelja','2 lista pergamenta','Najstariji hrvatskoglagoljski homiletski tekst u Hrvatskoj',NULL,NULL),(29,'Vrbnički fragmenti brevijara',3,'Vrbnik','XIII. stoljeće','Ustavna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Dio temporala (II. i III. nedjelja došašća), fragmenti A i D pripadali su istom brevijaru, a B i C drugom rukopisu','4 lista pergamenta','Izdao ih je J. Vajs, a detaljno opisao V. Štefanić, fragmente je pisao jedan pisar, a služili su kao omoti dokumentima župne crkve u Vrbniku',NULL,NULL),(30,'Ročki glagoljski abecedarij',3,'Crkva Sv. Antuna u Roču','1200. godina','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Abecedarij (autohtoni)','Grafit na zidu crkve (uparan oštricom u posvetni križ)','Obuhvaća puni niz glagoljske azbuke iz formativnog razdoblja: 34 znaka; između ostalih latinsko M, dva znaka za nekadašnji poluglas (u obliku ključa, pojavljuje se i štapić), znak za prejotirani prednji nazal',NULL,NULL),(31,'Splitski fragment misala',3,'jugozapadna Bosna','XIII. stoljeće','Formativna glagoljica(predstandardizacijsko razdoblje - istočno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik (ikavskoštokavska jezična obilježja)','Misni obrasci iz sanktorala za mjesec prosinac','List pergamenta','Pronašao ga je V. Jagić, čuva se u Kaptolskom arhivu u Splitu',NULL,NULL),(32,'Legenda o Sv. Tekli',3,' ','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Odlomak apokrifa Djela Pavla i Tekle','Dvolist pergamenta','Čuva se u Arhivu HAZU u Zagrebu',NULL,NULL),(33,'Epistola o nedjelji',3,' ','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Krnji dvolist s odlomkom apokrifne Epistole o nedjelji','Dvolist pergamenta','Pretpostavlja se da je dio okrnjenog zbornika, nađena je na koricama Korizmenjaka Šimuna Grebla u Vrbniku, mlađa od Kukuljevićeva odlomka misala, čuva se u Arhivu HAZU',NULL,NULL),(34,'Istarski razvod',3,'Istra','1275. - 1395. godina','Kurzivna glagoljica','Tri verzije: latinski, njemački i hrvatski jezik','Pravni akt u kojemu su utvrđene međe istarskih posjeda Venecije, akvilejskoga patrijarha i pazinskih grofova','35 listova','Pruža podatke o etničkoj i društvenoj slici srednjovjekovne Istre, danas je sačuvan hrvatski prijepis Levca Križanića iz 1546. godine, čuva se u NSK u Zagrebu',NULL,NULL),(35,'Homilija na Blagovijest',3,' ','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Sadrži dvije homilije, propovijedi na blagdan Blagovijesti','Dva lista pergamenta','Čuva se u Arhivu HAZU u Zagrebu',NULL,NULL),(36,'Zagrebački fragment brevijara',3,' ','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Odlomci sanktorala','Dvolist oštećenog pergamenta','Čuva se u Arhivu HAZU u Zagrebu',NULL,NULL),(37,'Riječki fragment Mudrih izreka',3,'Rijeka','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Ostatak temporala hrvatskoglagoljskog brevijara','Dio pergamentnog lista','Pronašla ga je Anica Vlašić-Anić, nalazi se u Knjižnici kapucinskog samostana Gospe Lurdske u Rijeci',NULL,NULL),(38,'Omišaljski list apostola',3,'Krk','XIII. - XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Poslanica Sv. Pavla apostola Rimljanima','List pergamenta','Jako važan spomenik za hrvatsko glagoljaštvo jer dokazuje da su u okviru hrvatskoga, zapadno orijentiranoga glagoljaštva postojali cjeloviti apostoli, što je inače svojstveno istočnoj Crkvi',NULL,NULL),(39,'Prvi vrbnički brevijar',3,'Vrbnik','XIII. - XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik (s mnogo arhaičnih staroslavenizama, ali i elemenata govornoga hrvatskoga jezika)','Slijedi skraćeni oficij rimske kurije, važan za proučavanje biblijskih tekstova','259 pergamentnih listova','Sadrži lokalna dijalektna obilježja vrbničkog područja, čuva se u Župnom uredu u Vrbniku',NULL,NULL),(40,'Birbinjski fragment misala',3,'Dugi otok','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, perikope na Veliki petak','List pergamenta','Čuva se u Sanktpeterburgu, nalazi se u Berčićevoj zbirci',NULL,NULL),(41,'Krčki fragment pasionala',3,'Krk','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Pasija četrdeset sebastenskih mučenika, odlomak Mučenja Sv. Jurja, odlomak Pseudo-Prohorovih Djela Ivanovih i odlomak neke homilije','3 lista pergamenta + 1 manji dio lista','Nalazi se u Arhivu (bivše) Staroslavenske akademije',NULL,NULL),(42,'Belski fragment misala',3,'Cres','XIII. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Dio misala, nepotpuna misa u utorak po Uskrsu','Oštećeni komad pergamenta','Jedan od najstarijih odlomaka glagoljskih misala hrvatske redakcije',NULL,NULL),(43,'Glagoljski grafiti u Sv. Martinu u Lovčiću',3,'Lovčić kraj Slavonskog Broda','XIII. - XIV. stoljeće','Ustavna/formativna glagoljica','Hrvatskostaroslavenski jezik','Tri grafita u kojima je ispisano nekoliko slova, a u posljednjem grafitu vidljive su riječi TLINA, BRT, MRT','Tri kamena grafita','Milan Paun je pronašao i istražio grafite, smatra da slova upućuju na sveca Sv. Martina',NULL,NULL),(44,'Kirinov/Lobkovicov psaltir',4,'Lika','1359. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Zbirka psalama, kantici, Atanazijevo vjerovanje, Te Deum, komunal, misa u čast Sv. Trojstva, mise za mrtve, mise u čast Blažene Djevice Marije, litanije svih svetih, Apostolsko vjerovanje','160 listova pergamenta','Nazvan po posljednjim vlasnicima iz obitelji Lobkovic, u kolofonu na kraju rukopisa očuvala se bilješka o pisaru, žaknu Kirinu iz crkve Sv. Kuzme i Damjana u Senju, najstarija poznata knjiga psalama, čuva se u Narodnom muzeju u Pragu',NULL,NULL),(45,'Misal kneza Novaka',4,'Lika','1368. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Sadrži temporal, red mise s kanonom, zavjetne mise, sanktoral, komunal, ritualne tekstove i molitve','271 list pergamenta','Pisao ga je knez Novak Disislavić, dvorski vitez kralja Ludovika I., u tom kodeksu je hrvatska ustavna glagoljica dosegla vrhunac pismovnog razvoja, obiluje sjajno izvedenim inicijalima, poznat je po tome što su u njemu zabilježeni i prvi hrvatski čakavski osmerački stihovi, poslužio kao osnovni predložak za prvu hrvatsku tiskanu knjigu Misal po zakonu rimskoga dvora čuva se u Austrijskoj nacionalnoj knjižnici',NULL,NULL),(46,'Bula pape Grgura XI. pavlinima',4,'Avignon','1371. godina','Poluustavna/kurzivna glagoljica','Latinski jezik (prevedena na hrvatski za hrvatske samostane pavlinskoga reda u Hrvatskom primorju)','Utvrđuje pravo vlasništva pavlinskoga reda na sve posjede, kao i na prihode od dara almozine iz raznih izvora, govori se i o pravu na pokop unutar zidina samostana, o načinu izbora novoga priora, otkupu prava na desetinu i samostanske posjede od svjetovnjaka i drugo',' ','Izvorno napisana na latinskom jeziku, čuva se u NSK u Zagrebu',NULL,NULL),(47,'Sokolska listina',4,'Zapadna Bosna','1380. godina','Poluustavna glagoljica','Starohrvatski jezik',' ','List pergamenta - dokument',' ',NULL,NULL),(48,'Senjski statut',4,'Senj','1388. godina','Prijepis hrvatskoga prijevoda izvornoga latinskog teksta statuta iz 1388. godine, do suvremenih primatelja dolazi u Ručićevu latiničnom prijepisu iz 1701. godine, vjerojatna na glagoljičnom pismu','Starohrvatski jezik','130 članaka o pravima senjskih plemića (Stjepan i Ivan Frankapan i drugi)','22 lista pergamenta','Latinski original čuvao se u Arhivu grada Senja do II. svjetskog rata, poslije toga nestao',NULL,NULL),(49,'Počiteljska isprava',4,'Počitelj u Lici','1393. godina','Rana kurzivna glagoljica','Starohrvatski jezik','Isprava/dokument','1 list','Čuva se u Arhivu HAZU u Zagrebu',NULL,NULL),(50,'Reimski zbornik',4,'Reims','1395. godina','Ustavna glagoljica (i ćirilica)','Hrvatsko-staroslavenski jezik','Lekcionar za veće blagdane, namijenjen pontifikalnoj misi emauskih opata','31 pergamentni list ustavne glagoljice','Jedan opat zapisao je kako je prvi dio zbornika pisan srpskim jezikom (ćirilicom), a drugi ilirskim, odnosno hrvatskim jezikom, na njemu su kraljevi prisezali kruneći se, a taj glagoljički dio je hrvatskog podrijetla, i iz škole hrvatskih benediktinaca, ali ga je prepisao neki Čeh 1395. godine u praškom samostanu Emausu, čuva se u Gradskoj knjižnici u Reimsu',NULL,NULL),(51,'Kožljačko-mošćenički razvod',4,'Mošćenica, Istra','1395. godina','Ustavna (i kurzivna) glagoljica','Starohrvatski jezik',' ',' ','Najstariji sačuvani glagoljski tekst iz mošćeničke župe',NULL,NULL),(52,'Brevijar Vida Omišljanina',4,'Krk','1396. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Sadrži službe za blagdane Gospodnje i za nedjelje, opširni biblijski tekstovi','468 listova pergamenta','Teži gotovo 10 kilograma; sadrži grecizme uz latinske i bosanske utjecaje, pisao ga je Vid iz Omišlja za crkvu u Roču, čuva se u Austrijskoj nacionalnoj knjižnici',NULL,NULL),(53,'Grafiti u Brodskom Drenovcu kod Požege',4,'Brodski Drenovac','XIV. - XV. stoljeće','Formativna/ustavna glagoljica','Hrvatskostaroslavenski i starohrvatski jezik','Zabilježena slova s vanjske istočne strane i oko crkvenih vrata (DRAGO, VIOT, EČR, TORO; RAB; U DRÊNOVÊCE TO E PISA BRT RÊČIĆ i dr.)','4 grafita','Četiri grafita nalaze se na gotičkom kustodiju, na jednom su kamenu prikazana tri kralja kako se odlaze pokloniti Isusu',NULL,NULL),(54,'Vatikanski brevijar Illirico 5',4,'Lika','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, kalendar','248 listova pergamenta','Čuva se u Apostolskoj knjižnici u Vatikanu, pisan u Tribihovićima u Lici, Turci su ga zaplijenili pa su dobri muž Okrugljane i Tribihovićane otkupili 1487. godine',NULL,NULL),(55,'Vatikanski brevijar Illirico 6',4,'Lika','XIV. stoljeće (1379.?)','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Psaltir, kantici i himni, općinstvo svetih, oficij za mrtve, ritualni tekstovi, sanktoral','212 listova pergamenta','Čuva se u Apostolskoj knjižnici u Vatikanu, pisao ga je pisar Fabijan u Tribihovićima, u vrijeme kneza Anža',NULL,NULL),(56,'Pašmanski brevijar',4,'Pašman','XIV. - XV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, sanktoral, psaltir s kanticima, i himnima, kalendar, komunal','414 listova pergamenta','Jedan od rijetkih potpuno očuvanih glagoljskih kodeksa, čuva se u Arhivu HAZU u Zagrebu',NULL,NULL),(57,'Fragment govora o svetkovanju nedjelje',4,' ','XIV. stoljeće','Poluustavna glagoljica','Hrvatskostaroslavenski jezik','Odlomak govora o svetkovanju nedjelje, potvrde iz oba zavjeta, kritika svećenstva','Dvolist pergamenta','Govor je preveo Hrvat u emauskom periodu s češkog jezika (zbog toga sadrži riječi češkog jezika)',NULL,NULL),(58,'Pazinski fragmenti',4,'Pazin','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Odlomak Legende o Sv. Eustahiju, odlomak apokrifa o križnom drvetu, odlomci apokrifa Uspenje Bogorodice, odlomci apokrifnog Nikodemova evanđelja','17 pergamentnih fragmenata','11 fragmenata pronašao je Ivan Kukuljević Sakcinski u XIV. stoljeću, a ostalih 6 pronađeno je 2005. godine u Pazinu',NULL,NULL),(59,'Ivančićev zbornik',4,' ','XIV. - XV. stoljeće','Ustavna glagoljica','Hrvatsko-staroslavenski jezik (mješavina hrvatskočakavskih i staroslavenskih elemenata)','Sadrži Marijina čudesa, Pitanja i odgovore, Riči od nauka svetih otaca i Blagoslov stola','181 list pergamenta','Služio za izobrazbu glagoljaškog svećeničkog podmlatka, naziv dobio po Stjepanu Ivančiću koji ga je prvi otkrio, čuva se u Samostanu franjevaca trećoredaca na Ksaveru u Zagrebu',NULL,NULL),(60,'Čajnički evanđelistar',4,'Čajniče, jugoistočna Bosna','XIV. - XV. stoljeće','Glagoljica u ćiriličnom kodeksu – primjer retardirane/degenerirane glagoljice kakva se u Bosni rabila u XIII. stoljeću','Bosanska redakcija staroslavenskoga jezika','Evanđelistar','Sačuvano 167 listova pergamenta','Osim evanđelistara, sadrži i nepotpunu i deformiranu glagoljičku azbuku; pripadao obitelji Rađenović, nalazi se u Muzeju Crkve Uspenja Bogorodice i Crkve Vaznesenja Hristovog u Čajniču',NULL,NULL),(61,'Hrvatski glagoljički fragment iz Beineckea',4,'Beinecke, SAD','XIV. - XV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Dvolist misalskog fragmenta; čitanja za Bdijenje Svih svetih (Otkrivenje 5.6-12)','Dvolist pergamenta (fragment)',' ',NULL,NULL),(62,'Kopenhagenski misal',4,'Roč u Istri ili Krk','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Misal i abecedarij (temporal - nepotpun, kanon mise - nepotpun, kalendar, komunal, zavjetne mise, mise za mrtve, sanktoral)','263 folije','Rukopis se 1499. nalazio u Roču u Istri, imao je više predložaka i pisara i zbog toga je nepoznata lokacija, u XIX. stoljeću završio je u Austrijskoj nacionalnoj knjižnici u Beču, danas se čuva u Danskoj kraljevskoj knjižnici u Kopenhagenu',NULL,NULL),(63,'Pariška pjesmarica (Psaltir pariškog kodeksa)',4,'Sjeverna Hrvatska, Istra ili Primorje','XIV. stoljeće (1380.)','Ustavna glagoljica','Hrvatsko-staroslavenski jezik','Kalendar, brevijar (psaltir, komunal), misal, ritualni tekstovi, zbirka duhovnih pjesama (pjesmarica koju čini 9 duhovnih pjesama)','200 listova pergamenta','Pjesme prvi put objavio Josef Vajs 1905., čuva se u Nacionalnoj knjižnici u Parizu',NULL,NULL),(64,'Akademijin brevijar',4,'Istra','XIV. stoljeće (1384.?)','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Psaltir, kantici, himni, komunal, mise za mrtve, Marijin oficij i kalendar','70 listova pergamenta','Čuva se u Arhivu HAZU u Zagrebu',NULL,NULL),(65,'Krčki/Vrbnički/Vrbanski statut',4,'Krk','XIV. stoljeće (1388.)','Ustavna/kurzivna glagoljica i latinički kurziv','Starohrvatski jezik','Službeni dokument kojim su regulirana pravna pitanja u vrbničkoj općini','4 sveska s različitim brojem stranica','Statut privukao pozornost pravnika, povjesničara i filologa, izdan više puta u Hrvatskoj, a restauriran 1949. godine, po starosti drugi statut u Hrvatskoj, odmah iza Vinodolskog zakona čuva se u NSK u Zagrebu',NULL,NULL),(66,'Regula Sv. Benedikta',4,'Čokovac kod Tkona na otoku Pašmanu','XIV. stoljeće','Poluustavna glagoljica','Hrvatsko-staroslavenski jezik (s mnoštvom čakavizama)','Pravila benediktinskog reda','60 pergamentnih listova','Rukopis je prvi izdao Armin Pavić 1875. godine u Starinama, prepisana s mnogo starijeg predloška u benediktinskoj opatiji Sv. Kuzme i Damjana, čuva se u HAZU',NULL,NULL),(67,'Vatikanski misal Illirico 4',4,'Omišalj','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, kalendar, red mise, kanon mise, sanktoral, komunal, zavjetne mise, mise za mrtve, ritualni tekstovi','278 listova pergamenta','Najstariji cjeloviti sačuvani hrvatskoglagoljski misal, čuva se u Vatikanskoj knjižnici',NULL,NULL),(68,'Odlomak hrvatskoglagoljičkog misala u Krakovu',4,'Krakov','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Dijelovi misa na Obraćenje Sv. Pavla i na blagdan Sv. Petra i Pavla i dio mise za mrtve','3 lista pergamenta','Hrvatski glagoljaši djelovali u samostanu Kleparz u Krakovu, u požaru 1584. oštećen samostan, sačuvano vrlo malo glagoljičnih rukopisa, čuva se u Jagelonskoj knjižnici u Krakovu',NULL,NULL),(69,'Fragment iz Novoga Mesta',4,'Novo Mesto','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Odlomak legende o Sv. Eustahiju','Dio lista pergamenta','Odlomak je skinut s korica Rojstne matične knjige, uz Pazinske fragmente drugi hrvatskoglagoljski fragment legende o Sv. Eustahiju',NULL,NULL),(70,'Fojnički glagoljski fragment brevijara',4,' ','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Dio Knjige Izlaska i Slovo Sv. Ivana Zlatoustoga','Oštećeni list pergamenta','Pronašao ga je Josip Hamm 1950. godine u Muzeju Franjevačkog samostana u Fojnici, danas je izgubljen',NULL,NULL),(71,'Drugi vrbnički brevijar',4,'Vrbnik','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Nepotpuni temporal','295 listova pergamenta','Tekst su pisale tri ili četiri ruke',NULL,NULL),(72,'Četvrti vrbnički brevijar',4,'Vrbnik','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Sanktoral i komunal','112 listova pergamenta','Nedostaje oko 150 listova, čuva se u Vrbniku od 1605. godine',NULL,NULL),(73,'Padovanski brevijar',4,' ','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal','308 listova pergamenta','Pisalo ga je najmanje pet pisara, nalazi se u Padovi',NULL,NULL),(74,'Medicejski brevijar',4,' ','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Psaltir s kanticima, laude i kantici, komunal, temporal, mise za mrtve, Dies irae','124 lista pergamenta','Nalazi se u Firenci',NULL,NULL),(75,'Oxfordski brevijar-misal',4,' ','XIV. stoljeće','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Brevijar, misal i ritual','410 listova pergamenta','Ispisan neuobičajeno sitnim glagoljskim slovima, do nedavno datiran u 1310. godinu, ali su istraživači u novije vrijeme skeptični pa je datiran okvirno',NULL,NULL),(76,'Glagoljički abecedarij Jurja Slovinca',5,'Tours, Francuska','1400. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik (naglašeno ikaviziran)','Abecedarij i osnovne kršćanske molitve glagoljičkim pismom','Mala knjižica/glagoljaška početnica','Juraj Slovinac (Georgius de Sclavonia) bio je profesor na pariškom sveučilištu Sorbonne, po sjećanju napisao abecedarij i molitve kako bi kolegama pokazao da postoji izvorno hrvatsko pismo',NULL,NULL),(77,'Berlinski misal',5,'Lika','1402. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, red mise, zavjetne mise, kalendar, sanktoral, komunal i ritualni tekstovi','218 pergamentnih listova','Najstariji očuvani rukopis Bartola Krbavca, listovi oslikani minijaturama, čuva se u Državnoj knjižnici u Berlinu',NULL,NULL),(78,'Glagoljska isprava iz Ostrožca kod Bihaća',5,'Ostrožac kod Bihaća','1403. godina','Poluustavna glagoljica','Starohrvatski jezik',' ','Pergament s pečatom na vrpci od ljubičaste svile','Čuva se u arhivu knezova Auersperg u Ljubljani',NULL,NULL),(79,'Hrvojev misal',5,'Zadarsko područje','1404. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik (s mnogo kroatizama)','Temporal, red mise, kalendar, sanktoral, komunal, zavjetne mise i ritualni tekstovi','247 pergamentnih listova','Napisao ga je pisar Butko za Hrvoja Vukčića Hrvatinića, sadrži puno iluminacija od urešenih i oslikanih inicijala, nakon osmanskog osvojenja Bosne, nalazio se u Budimu, a potom je dospio u Istanbul gdje se danas čuva u Topkapi Sarayi',NULL,NULL),(80,'Dragućki brevijar',5,'Krbavsko-zadarski krug','1407. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, rubrika, himni, kalendar, dodani oficiji','196 pergamentnih listova','Pisalo ga je više ruku, mjesto pisanja se smješta u krbavsko područje zbog brojnih ikavizama, a kasnije je tek donesen u Istru u Draguć, iluminiran, sadrži ilustracije uskrsnuća, proroka i orla',NULL,NULL),(81,'Bakarski brevijar',5,'Bakar','1414. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Sadrži crkvenu kroniku Bakra',' ','Pisao ga je Bartol Krbavac, izgubljen je',NULL,NULL),(82,'Češka glagoljska Biblija',5,' ','1416. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik (i češki)','Sadrži dijelove najstarijeg češkog prijevoda Biblije','258 pergamentnih folija','Autori su češki učenici hrvatskih glagoljaša što u knjizi izrijekom navode',NULL,NULL),(83,'Ročki misal',5,'Roč, Istra','Oko 1420. godine','Ustavna glagoljica','Hrvatskostaroslavenski jezik','Temporal, kalendar, red mise, kanon mise, sanktoral, komunal, zavjetne mise, mise za mrtve, ritualni tekstovi','232 lista pergamenta','Autor je Bartol Krbavac, 13 listova napisao je neki drugi pisac, misal je bogat iluminacijama, u Roču se rabio do 1593. godine, a danas se čuva u Austrijskoj nacionalnoj knjižnici u Beču',NULL,NULL),(84,'Barbanski misal',5,'Barban, Istra','1425. godina','Ustavna glagoljica','Hrvatskostaroslavenski jezik',' ','1 list (ostali izgubljeni)','Autor je Urban, barbanski župnik',NULL,NULL),(85,'Zakon brašćine sv. Marije Goričke u Baški',5,'Baška','1425. godina','Ustavna glagoljica','Starohrvatski jezik (krčka čakavica s jakim utjecajem staroslavenskoga jezika)','Pravila reda','Sedam listova od kojih su dva izgubljena','Najstariji sačuvani statut jedne bratovštine napisan glagoljicom',NULL,NULL),(86,'Listina iz Like',5,'Lika','1433. godina','Kurzivna glagoljica','Starohrvatski jezik','Matijica Utišenić os