INSERT INTO `zapis_jezik` VALUES (1,1,'hr','Pariški abecedarij',1,' ','XI. stoljeće','Obla glagoljica','Latinski jezik','Imena 31 glagoljskog slova pisana latinicom',' ','U literaturi se ponekad naziva i bugarskim, tj. Abecedarium bulgaricum, najstariji je ksenografski abecedarij što znači da ga je sastavljao stranac s namjerom upoznavanja nove publike s glagoljičkim pismom',NULL,NULL),(2,2,'hr','Valunska ploča',1,'Crkva Sv. Marije u Valunu na Cresu','XI. stoljeće','Glagoljica i latinica','Hrvatskostaroslavenski i latinski jezik','Nadgrobni natpis','Kamena ploča','Dvojezični i dvopismeni nadgrobni natpis',NULL,NULL),(3,3,'hr','Kločev glagoljaš',1,' ','XI. stoljeće','Obla glagoljica uz neke uglate oblike','Hrvatskostaroslavenski jezik','Pet homilija','14 listova kodeksa (najvjerojatnije sadržavao 552)','Spomenik koji ima neke uglate glagoljične oblike, sadrži originalnu Anonimnu homiliju za koju se pretpostavlja da je autor Sv. Metod',NULL,NULL),(4,4,'hr','Plominski natpis',1,'Crkva Sv. Jurja u Plominu','XI. stoljeće','Obla glagoljica','Hrvatskostaroslavenski jezik','Fragmentarno uklesane 4 riječi autora: SE E PISЪLЪ S','Kameni reljef','Tekst na reljefu s uklesanim likom Silvana/Sv. Jurja',NULL,NULL),(5,5,'hr','Konavoski glagoljski natpis',1,'Dunave u Konavlima','XI. stoljeće','Obla glagoljica','Hrvatskostaroslavenski jezik','Natpis čiji sadržaj govori o nastanku tog spomenika','Kamena ploča','Istočno (južno) krilo glagoljaštva, pretpostavlja se da je nastao 1060. godine, čuva se u Arheološkom muzeju u Dubrovniku',NULL,NULL),(6,6,'hr','Krčki natpis',1,'Krk','XI. stoljeće','Obla glagoljica','Hrvatskostaroslavenski jezik','Natpis o gradnji crkve uz benediktinski samostan','Kamena ploča','Ploča na kojoj su zapisana imena graditelja benediktinskog samostana (maĵъ, opatъ radonê, rugota, dobroslav[ъ]))',NULL,NULL),(7,7,'hr','Župski glagoljski natpis',1,'Župa Dubrovačka','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Nepoznat, neistražen','Kamena ploča','Glagoljski natpis na ulomku antičkog crijepa (teguli), pronađen kraj crkve Sv. Đurđa između mjesta Petrače i Buića u Župi Dubrovačkoj; još neistražen u potpunosti, čuva se u Arheološkom muzeju u Dubrovniku',NULL,NULL),(8,8,'hr','Jurandvorski ulomci',1,'Crkva Sv. Lucije u Jurandvoru, Krk','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Nepoznati dijelovi ulomaka, spominje se kralj Zvonimir','4 ulomka ploče','Popularno se još zove i druga Bašćanska ploča',NULL,NULL),(9,9,'hr','Senjska ploča',1,'Senj','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Ulomci teksta, sadrži kršćansku invokaciju','Ploča, gornji dio lijevog pluteja (oltarne pregrade)','Motivi lozice, pletera, desetak redaka teksta, pronađena prilikom konzerviranja tvrđave Nehaj 1964. godine',NULL,NULL),(10,10,'hr','Grdoselski ulomak',1,'Istra','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Spominje se pop Golob iz Tinjana, kapelan grdoselskog kneza Pankracija','Ulomak kamene grede ili ploče s dijelom glagoljskoga natpisa','Prijelazni tip oble u uglatu glagoljicu, mali ulomak gdje je posveta oltara u čast dvjema sveticama',NULL,NULL),(11,11,'hr','Kninski ulomak',1,'Crkva Sv. Bartola u Kapitulu kod Knina','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Ulomak luka s par glagoljičnih i ćiriličnih slova: oĉju premi','Maleni ulomak ukrašen romaničkom lozicom','Miješanje glagoljice i ćirilice',NULL,NULL),(12,12,'hr','Plastovski odlomak',1,'Plastovo kod Skradina','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje – rub zapadnog područja hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Maleni ulomak s tekstom: P E T R A ? I J U M U U Ž U T U E G A B R A','Maleni kameni ulomak','Miješanje glagoljičnih i ćiriličnih slova',NULL,NULL),(13,13,'hr','Bečki listići',1,' ','XI. - XII. stoljeće','Formativna glagoljica (predstandardizacijsko razdoblje - zapadno područje hrvatskoga glagoljaštva)','Hrvatskostaroslavenski jezik','Odlomak gregorijanskog sakramentara (misala) zapadnog obreda, sadrže tri mise u čast apostola','Dva lista pergamenta','Po sadržaju bliski Kijevskim listićima (liturgija Sv. Petra), svjedoče o češko-hrvatskim uljudbenim vezama; U literaturi se nerijetko ističe kako su Bečki listići najstariji hrvatskoglagoljski tekst liturgijske namjene; našao ih je i opisao Vatroslav Jagić 1890. godine',NULL,NULL),(14,14,'hr','Prva stranica Kijevskih listića',1,' ','XI. - XII. stoljeće','Obla/formativna glagoljica (istočno područje hrvatskoga glagoljaštva)','Staroslavenski/Hrvatskostaroslavenski jezik','Poslanica Sv. Pavla apostola Rimljanima i oracije Blažene Djevice Marije (prepletanje istočne i zapadne tradicije)','Listovi neujednačene veličine','Kijevski listići drže se najstarijim spomenikom kanona starocrkvenoslavenskih spisa, nastali su na češkom području i broje ukupno 13 stranica, prva stranica pisana je kasnije na hrvatskom prostoru i zaključuje se da je taj dio sakramentara bio u uporabi na dubrovačkom području',NULL,NULL),(15,15,'hr','Glagoljički natpis iz Čečavca kod Slavonske Požege',1,'Čečavac','XI. - XII. stoljeće','Formativna glagoljica','Hrvatskostaroslavenski jezik','Četiri se glagoljična slova tumače kao godina prema brojevnoj vrijednosti ČRIZ = 1129','4 glagoljična slova na komadu opeke','Potječe iz romaničke benediktinske opatije Sv. Miholvila u Rudini. Čuva se u Muzeju Požeške kotline u Požegi',NULL,NULL),(16,16,'hr','Bašćanska ploča',2,'Jurandvor kod Baške na otoku Krku','1100. godina','Formativna glagoljica (preds